import React, { useState } from 'react';
import { useTemplateStore } from './store/templateStore';
import { TemplateGenerator } from './utils/templateGenerator';
import { PDFExporter } from './utils/pdfExporter';
import ControlPanel from './components/ControlPanel';
import TemplateCanvas from './components/TemplateCanvas';
import { PenTool, Download, AlertCircle } from 'lucide-react';

function App() {
  const [isExporting, setIsExporting] = useState(false);
  const [exportStatus, setExportStatus] = useState('');
  const config = useTemplateStore();

  const handleExportPDF = async () => {
    setIsExporting(true);
    setExportStatus('Generating PDF...');

    try {
      const templateGenerator = new TemplateGenerator(config);
      const pdfExporter = new PDFExporter(templateGenerator, config);

      const success = await pdfExporter.exportToPDF('handwriting-template.pdf');

      if (success) {
        setExportStatus('PDF exported successfully!');
      } else {
        setExportStatus('Failed to export PDF. Please try again.');
      }
    } catch (error) {
      console.error('Export error:', error);
      setExportStatus('Error occurred during export.');
    } finally {
      setIsExporting(false);
      setTimeout(() => setExportStatus(''), 3000);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <PenTool className="w-8 h-8 text-primary-600" />
              <div>
                <h1 className="text-xl font-bold text-gray-900">
                  Handwriting Template Generator
                </h1>
                <p className="text-sm text-gray-600">
                  Create custom practice templates for italic and calligraphy scripts
                </p>
              </div>
            </div>

            {exportStatus && (
              <div className="flex items-center space-x-2 px-3 py-2 rounded-md bg-blue-50 text-blue-700">
                <AlertCircle className="w-4 h-4" />
                <span className="text-sm">{exportStatus}</span>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex gap-8">
          {/* Control Panel */}
          <div className="flex-shrink-0">
            <ControlPanel onExportPDF={handleExportPDF} />
          </div>

          {/* Canvas Area */}
          <TemplateCanvas />
        </div>
      </main>

      {/* Export Loading Overlay */}
      {isExporting && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-8 flex flex-col items-center space-y-4">
            <Download className="w-12 h-12 text-primary-600 animate-bounce" />
            <div className="text-center">
              <h3 className="text-lg font-semibold text-gray-900">Exporting PDF</h3>
              <p className="text-gray-600">Please wait while we generate your template...</p>
            </div>
            <div className="w-64 bg-gray-200 rounded-full h-2">
              <div className="bg-primary-600 h-2 rounded-full animate-pulse" style={{ width: '100%' }}></div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
