import React, { useState, useRef, useCallback } from 'react';
import { Download, RotateCcw, Printer } from 'lucide-react';

const HandwritingTemplateGenerator = () => {
  // A4 dimensions in mm
  const A4_WIDTH = 210;
  const A4_HEIGHT = 297;
  const SCALE_FACTOR = 2.8; // Scale for screen display

  // State for template settings
  const [settings, setSettings] = useState({
    // Typography metrics (in mm)
    xHeight: 4,
    capHeight: 6,
    ascenderHeight: 8,
    descenderDepth: 3,
    lineSpacing: 15, // Increased for better separation
    lineGroupSpacing: 25, // Space between groups of lines

    // Layout settings
    slantAngle: 7, // degrees
    marginTop: 20,
    marginBottom: 20,
    marginLeft: 25, // More space for labels
    marginRight: 15,

    // Grid options
    showSlantLines: true,
    showGrid: false, // Default to minimal
    showXHeight: true, // Show by default like reference
    showCapHeight: false, // Not shown in reference
    showAscender: true, // Show A line
    showDescender: true, // Show D line
    showLineLabels: true, // New feature for A, X, D labels
    gridSpacing: 5,
    slantLineSpacing: 4, // Spacing between diagonal lines

    // Line styles
    baselineOpacity: 1.0,
    guidelineOpacity: 0.4,
    slantLineOpacity: 0.4, // More visible like in reference

    // Colors
    baselineColor: '#000000',
    guidelineColor: '#0066cc',
    slantLineColor: '#ff6600'
  });

  const svgRef = useRef();

  // Typography presets
  const presets = {
    minimal: {
      xHeight: 4,
      capHeight: 6,
      ascenderHeight: 8,
      descenderDepth: 3,
      lineSpacing: 15,
      lineGroupSpacing: 25,
      slantAngle: 7,
      slantLineSpacing: 4,
      showXHeight: false,
      showCapHeight: false,
      showAscender: false,
      showDescender: false,
      showLineLabels: false,
      showGrid: false
    },
    reference: {
      xHeight: 4,
      capHeight: 6,
      ascenderHeight: 8,
      descenderDepth: 3,
      lineSpacing: 15,
      lineGroupSpacing: 25,
      slantAngle: 7,
      slantLineSpacing: 4,
      showXHeight: true,
      showCapHeight: false,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: false
    },
    copperplate: {
      xHeight: 3,
      capHeight: 6,
      ascenderHeight: 9,
      descenderDepth: 3,
      lineSpacing: 18,
      lineGroupSpacing: 30,
      slantAngle: 12,
      slantLineSpacing: 3.5,
      showXHeight: true,
      showCapHeight: true,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: false
    },
    advanced: {
      xHeight: 4,
      capHeight: 6,
      ascenderHeight: 8,
      descenderDepth: 3,
      lineSpacing: 15,
      lineGroupSpacing: 25,
      slantAngle: 7,
      slantLineSpacing: 4,
      showXHeight: true,
      showCapHeight: true,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: true
    }
  };

  // Update settings
  const updateSetting = (key, value) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  // Apply preset
  const applyPreset = (presetName) => {
    const preset = presets[presetName];
    setSettings(prev => ({ ...prev, ...preset }));
  };

  // Calculate line positions with proper grouping
  const calculateLines = () => {
    const { marginTop, marginBottom, lineSpacing, lineGroupSpacing, xHeight, capHeight, ascenderHeight, descenderDepth } = settings;
    const availableHeight = A4_HEIGHT - marginTop - marginBottom;

    // Calculate lines per group (typically 3-4 lines per group for practice)
    const linesPerGroup = 3;
    const totalGroupHeight = (linesPerGroup - 1) * lineSpacing + lineGroupSpacing;
    const groupsCount = Math.floor(availableHeight / totalGroupHeight);

    const lines = [];

    for (let group = 0; group < groupsCount; group++) {
      for (let lineInGroup = 0; lineInGroup < linesPerGroup; lineInGroup++) {
        const baselineY = marginTop + (group * totalGroupHeight) + (lineInGroup * lineSpacing) + lineSpacing;

        lines.push({
          baseline: baselineY,
          xHeightLine: baselineY - xHeight,
          capHeightLine: baselineY - capHeight,
          ascenderLine: baselineY - ascenderHeight,
          descenderLine: baselineY + descenderDepth,
          groupIndex: group,
          lineIndex: lineInGroup
        });
      }
    }

    return lines;
  };

  // Generate slant lines that fill only the grid/writing area
  const generateSlantLines = () => {
    const { slantAngle, slantLineSpacing, marginLeft, marginRight, marginTop, marginBottom } = settings;
    const slantLines = [];

    const angleRad = (slantAngle * Math.PI) / 180;
    const tanAngle = Math.tan(angleRad);

    // Define the writing area boundaries
    const writingAreaLeft = marginLeft;
    const writingAreaRight = A4_WIDTH - marginRight;
    const writingAreaTop = marginTop;
    const writingAreaBottom = A4_HEIGHT - marginBottom;
    const writingAreaWidth = writingAreaRight - writingAreaLeft;
    const writingAreaHeight = writingAreaBottom - writingAreaTop;

    // Calculate how much the lines extend due to the angle
    const heightProjection = writingAreaHeight * tanAngle;

    // Start from left edge of writing area minus projection
    const startX = writingAreaLeft - heightProjection;
    const totalWidth = writingAreaWidth + (2 * heightProjection);
    const numLines = Math.ceil(totalWidth / slantLineSpacing) + 2;

    for (let i = 0; i < numLines; i++) {
      const x = startX + (i * slantLineSpacing);

      // Calculate line endpoints within writing area
      const topX = x;
      const bottomX = x + heightProjection;

      slantLines.push({
        x1: topX,
        y1: writingAreaTop,
        x2: bottomX,
        y2: writingAreaBottom
      });
    }

    return slantLines;
  };

  // Generate grid lines
  const generateGridLines = () => {
    const { marginLeft, marginRight, marginTop, marginBottom, gridSpacing } = settings;
    const verticalLines = [];
    const horizontalLines = [];

    // Vertical grid lines
    const availableWidth = A4_WIDTH - marginLeft - marginRight;
    for (let x = 0; x <= availableWidth; x += gridSpacing) {
      verticalLines.push({
        x: marginLeft + x,
        y1: marginTop,
        y2: A4_HEIGHT - marginBottom
      });
    }

    // Horizontal grid lines
    const availableHeight = A4_HEIGHT - marginTop - marginBottom;
    for (let y = 0; y <= availableHeight; y += gridSpacing) {
      horizontalLines.push({
        x1: marginLeft,
        x2: A4_WIDTH - marginRight,
        y: marginTop + y
      });
    }

    return { verticalLines, horizontalLines };
  };

  // Export as SVG string for PDF generation
  const exportSVG = () => {
    const svgElement = svgRef.current;
    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(svgElement);

    // Create download link
    const blob = new Blob([svgString], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'handwriting-template.svg';
    link.click();
    URL.revokeObjectURL(url);
  };

  // Print function
  const printTemplate = () => {
    const svgElement = svgRef.current;
    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(svgElement);

    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Handwriting Template</title>
          <style>
            @page { 
              size: A4; 
              margin: 0; 
            }
            body { 
              margin: 0; 
              padding: 0; 
            }
            svg { 
              width: 210mm; 
              height: 297mm; 
            }
          </style>
        </head>
        <body>
          ${svgString}
        </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.print();
  };

  const lines = calculateLines();
  const slantLines = generateSlantLines();
  const { verticalLines, horizontalLines } = generateGridLines();

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Control Panel */}
      <div className="w-80 bg-white shadow-lg overflow-y-auto p-6 space-y-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-800 mb-2">Handwriting Template</h1>
          <p className="text-sm text-gray-600">Create custom A4 practice sheets for italic and script writing</p>
        </div>

        {/* Presets */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Style Presets</h3>
          <div className="grid grid-cols-2 gap-2">
            {Object.keys(presets).map(preset => (
              <button
                key={preset}
                onClick={() => applyPreset(preset)}
                className="px-3 py-2 bg-blue-100 text-blue-800 rounded-md hover:bg-blue-200 transition-colors text-sm capitalize"
              >
                {preset}
              </button>
            ))}
          </div>
        </div>

        {/* Typography Controls */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Typography Metrics (mm)</h3>
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-medium text-gray-700">Line Spacing: {settings.lineSpacing}mm</label>
              <input
                type="range"
                min="8"
                max="25"
                step="0.5"
                value={settings.lineSpacing}
                onChange={(e) => updateSetting('lineSpacing', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Group Spacing: {settings.lineGroupSpacing}mm</label>
              <input
                type="range"
                min="15"
                max="40"
                step="1"
                value={settings.lineGroupSpacing}
                onChange={(e) => updateSetting('lineGroupSpacing', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">X-Height: {settings.xHeight}mm</label>
              <input
                type="range"
                min="2"
                max="8"
                step="0.5"
                value={settings.xHeight}
                onChange={(e) => updateSetting('xHeight', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Cap Height: {settings.capHeight}mm</label>
              <input
                type="range"
                min="4"
                max="12"
                step="0.5"
                value={settings.capHeight}
                onChange={(e) => updateSetting('capHeight', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
          </div>
        </div>

        {/* Slant Angle */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Italic Angle</h3>
          <div className="space-y-3">
            <div>
              <label className="block text-sm font-medium text-gray-700">Slant Angle: {settings.slantAngle}</label>
              <input
                type="range"
                min="0"
                max="20"
                step="1"
                value={settings.slantAngle}
                onChange={(e) => updateSetting('slantAngle', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Diagonal Spacing: {settings.slantLineSpacing}mm</label>
              <input
                type="range"
                min="2"
                max="8"
                step="0.5"
                value={settings.slantLineSpacing}
                onChange={(e) => updateSetting('slantLineSpacing', parseFloat(e.target.value))}
                className="w-full"
              />
            </div>
          </div>
        </div>

        {/* Guide Lines */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Guide Lines</h3>
          <div className="space-y-2">
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showSlantLines}
                onChange={(e) => updateSetting('showSlantLines', e.target.checked)}
                className="mr-2"
              />
              Diagonal Lines
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showXHeight}
                onChange={(e) => updateSetting('showXHeight', e.target.checked)}
                className="mr-2"
              />
              X-Height Lines
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showCapHeight}
                onChange={(e) => updateSetting('showCapHeight', e.target.checked)}
                className="mr-2"
              />
              Cap Height Lines
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showAscender}
                onChange={(e) => updateSetting('showAscender', e.target.checked)}
                className="mr-2"
              />
              Ascender Lines
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showLineLabels}
                onChange={(e) => updateSetting('showLineLabels', e.target.checked)}
                className="mr-2"
              />
              Line Labels (A, X, D)
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showDescender}
                onChange={(e) => updateSetting('showDescender', e.target.checked)}
                className="mr-2"
              />
              Descender Lines
            </label>
            <label className="flex items-center">
              <input
                type="checkbox"
                checked={settings.showGrid}
                onChange={(e) => updateSetting('showGrid', e.target.checked)}
                className="mr-2"
              />
              Grid Lines
            </label>
          </div>
        </div>

        {/* Export Controls */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Export</h3>
          <div className="space-y-2">
            <button
              onClick={printTemplate}
              className="w-full flex items-center justify-center px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
            >
              <Printer className="w-4 h-4 mr-2" />
              Print Template
            </button>
            <button
              onClick={exportSVG}
              className="w-full flex items-center justify-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
            >
              <Download className="w-4 h-4 mr-2" />
              Download SVG
            </button>
          </div>
        </div>
      </div>

      {/* Preview Area */}
      <div className="flex-1 flex items-center justify-center p-8">
        <div className="bg-white shadow-xl" style={{ width: A4_WIDTH * SCALE_FACTOR, height: A4_HEIGHT * SCALE_FACTOR }}>
          <svg
            ref={svgRef}
            width={A4_WIDTH * SCALE_FACTOR}
            height={A4_HEIGHT * SCALE_FACTOR}
            viewBox={`0 0 ${A4_WIDTH} ${A4_HEIGHT}`}
            className="border border-gray-300"
          >
            {/* Page background */}
            <rect width={A4_WIDTH} height={A4_HEIGHT} fill="white" />

            {/* Define clipping path for writing area only */}
            <defs>
              <clipPath id="writingAreaClip">
                <rect 
                  x={settings.marginLeft} 
                  y={settings.marginTop} 
                  width={A4_WIDTH - settings.marginLeft - settings.marginRight} 
                  height={A4_HEIGHT - settings.marginTop - settings.marginBottom} 
                />
              </clipPath>
            </defs>

            {/* Slant lines - clipped to writing area only */}
            {settings.showSlantLines && (
              <g opacity={settings.slantLineOpacity} clipPath="url(#writingAreaClip)">
                {slantLines.map((line, i) => (
                  <line
                    key={`slant-${i}`}
                    x1={line.x1}
                    y1={line.y1}
                    x2={line.x2}
                    y2={line.y2}
                    stroke={settings.slantLineColor}
                    strokeWidth="0.15"
                  />
                ))}
              </g>
            )}

            {/* Grid lines */}
            {settings.showGrid && (
              <g opacity={settings.guidelineOpacity}>
                {verticalLines.map((line, i) => (
                  <line
                    key={`v-${i}`}
                    x1={line.x}
                    y1={line.y1}
                    x2={line.x}
                    y2={line.y2}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.1"
                    strokeDasharray="1,1"
                  />
                ))}
                {horizontalLines.map((line, i) => (
                  <line
                    key={`h-${i}`}
                    x1={line.x1}
                    y1={line.y}
                    x2={line.x2}
                    y2={line.y}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.1"
                    strokeDasharray="1,1"
                  />
                ))}
              </g>
            )}

            {/* Typography lines */}
            {lines.map((lineSet, i) => (
              <g key={`line-set-${i}`}>
                {/* Line Labels (A, X, D) - only for first line of each group */}
                {settings.showLineLabels && lineSet.lineIndex === 0 && (
                  <g opacity="0.6">
                    {/* Ascender label */}
                    {settings.showAscender && (
                      <text
                        x={settings.marginLeft - 8}
                        y={lineSet.ascenderLine + 1}
                        fontSize="3"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="Arial, sans-serif"
                        fontWeight="bold"
                      >
                        A
                      </text>
                    )}

                    {/* X-height label */}
                    {settings.showXHeight && (
                      <text
                        x={settings.marginLeft - 8}
                        y={lineSet.xHeightLine + 1}
                        fontSize="3"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="Arial, sans-serif"
                        fontWeight="bold"
                      >
                        X
                      </text>
                    )}

                    {/* Baseline/Descender label */}
                    <text
                      x={settings.marginLeft - 8}
                      y={lineSet.baseline + 1}
                      fontSize="3"
                      fill="#666"
                      textAnchor="middle"
                      fontFamily="Arial, sans-serif"
                      fontWeight="bold"
                    >
                      {settings.showDescender ? "D" : "B"}
                    </text>
                  </g>
                )}

                {/* Baseline - always shown */}
                <line
                  x1={settings.marginLeft}
                  y1={lineSet.baseline}
                  x2={A4_WIDTH - settings.marginRight}
                  y2={lineSet.baseline}
                  stroke={settings.baselineColor}
                  strokeWidth="0.4"
                  opacity={settings.baselineOpacity}
                />

                {/* X-height line */}
                {settings.showXHeight && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.xHeightLine}
                    x2={A4_WIDTH - settings.marginRight}
                    y2={lineSet.xHeightLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.2"
                    opacity={settings.guidelineOpacity}
                  />
                )}

                {/* Cap height line */}
                {settings.showCapHeight && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.capHeightLine}
                    x2={A4_WIDTH - settings.marginRight}
                    y2={lineSet.capHeightLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.15"
                    opacity={settings.guidelineOpacity}
                    strokeDasharray="3,2"
                  />
                )}

                {/* Ascender line */}
                {settings.showAscender && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.ascenderLine}
                    x2={A4_WIDTH - settings.marginRight}
                    y2={lineSet.ascenderLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.2"
                    opacity={settings.guidelineOpacity}
                  />
                )}

                {/* Descender line */}
                {settings.showDescender && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.descenderLine}
                    x2={A4_WIDTH - settings.marginRight}
                    y2={lineSet.descenderLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.2"
                    opacity={settings.guidelineOpacity}
                  />
                )}
              </g>
            ))}

            {/* Legend */}
            <g transform="translate(15, 15)">
              <rect x="0" y="0" width="45" height="18" fill="white" fillOpacity="0.95" stroke="#ddd" strokeWidth="0.1"/>
              <text x="2" y="4" fontSize="2.2" fill="#333" fontWeight="bold">Italic Practice Sheet</text>

              {settings.showLineLabels && (
                <>
                  <text x="2" y="8" fontSize="1.6" fill="#666">A = Ascender</text>
                  <text x="2" y="11" fontSize="1.6" fill="#666">X = X-height</text>
                  <text x="2" y="14" fontSize="1.6" fill="#666">{settings.showDescender ? "D = Descender" : "B = Baseline"}</text>
                </>
              )}

              {!settings.showLineLabels && (
                <>
                  <line x1="2" y1="8" x2="8" y2="8" stroke={settings.baselineColor} strokeWidth="0.4"/>
                  <text x="10" y="9" fontSize="1.6" fill="#333">Baseline</text>
                  {settings.showSlantLines && (
                    <>
                      <line x1="2" y1="12" x2="8" y2="12" stroke={settings.slantLineColor} strokeWidth="0.15"/>
                      <text x="10" y="13" fontSize="1.6" fill="#333">Slant {settings.slantAngle}</text>
                    </>
                  )}
                </>
              )}
            </g>
          </svg>
        </div>
      </div>
    </div>
  );
};

export default HandwritingTemplateGenerator;
