import React, { useState, useRef } from 'react';
import { Download, Printer, RotateCcw } from 'lucide-react';

const HandwritingTemplateGenerator = () => {
  // Default A4 dimensions in mm
  const DEFAULT_WIDTH = 210;
  const DEFAULT_HEIGHT = 297;
  const SCALE_FACTOR = 2.8; // Scale for screen display

  // State for page settings
  const [pageSettings, setPageSettings] = useState({
    orientation: 'portrait', // 'portrait' or 'landscape'
    customSize: false,
    width: DEFAULT_WIDTH,
    height: DEFAULT_HEIGHT
  });

  // Calculate current dimensions based on orientation and custom size
  const getCurrentDimensions = () => {
    if (pageSettings.customSize) {
      return {
        width: pageSettings.width,
        height: pageSettings.height
      };
    }

    // Standard A4 dimensions
    if (pageSettings.orientation === 'landscape') {
      return {
        width: DEFAULT_HEIGHT, // 297mm
        height: DEFAULT_WIDTH  // 210mm
      };
    } else {
      return {
        width: DEFAULT_WIDTH,  // 210mm
        height: DEFAULT_HEIGHT // 297mm
      };
    }
  };

  const { width: CURRENT_WIDTH, height: CURRENT_HEIGHT } = getCurrentDimensions();

  // State for template settings - Based on handwriting research
  const [settings, setSettings] = useState({
    // Typography metrics (in mm) - Research-based standards
    xHeight: 4, // Optimal for elementary students
    capHeight: 6, // 1.5x x-height for proper proportions
    ascenderHeight: 8, // 2x x-height for clear distinction
    descenderDepth: 3, // 0.75x x-height for balance
    lineSpacing: 15, // Adequate space to prevent ascender/descender overlap
    lineGroupSpacing: 25, // Clear separation between line groups

    // Layout settings - Educational best practices
    slantAngle: 7, // Optimal italic angle for beginners
    marginTop: 25, // Increased for better visual boundary
    marginBottom: 25, // Consistent margins
    marginLeft: 30, // More space for line labels and organization
    marginRight: 20, // Adequate right margin

    // Grid options - Research-informed settings
    showSlantLines: true,
    showGrid: false, // Default to minimal for clarity
    showXHeight: true, // Essential for proper letter formation
    showCapHeight: false, // Optional for advanced practice
    showAscender: true, // Important for tall letters
    showDescender: true, // Important for letters below baseline
    showLineLabels: true, // Educational value for learning
    gridSpacing: 5, // Fine grid for precision
    slantLineSpacing: 6, // Optimal spacing for letter width practice

    // Line styles - Enhanced visibility
    baselineOpacity: 1.0, // Always fully visible
    guidelineOpacity: 0.6, // More visible than before for better guidance
    slantLineOpacity: 0.5, // Balanced visibility

    // Colors - Research-based educational colors
    baselineColor: '#000000', // Black for strong baseline reference
    guidelineColor: '#0066cc', // Blue for secondary lines (common in education)
    slantLineColor: '#ff6600', // Orange for slant guides (high contrast)
    marginColor: '#ff0000' // Red for margins (warning/boundary color)
  });

  const svgRef = useRef();

  // Enhanced typography presets based on educational standards
  const presets = {
    beginner: {
      xHeight: 5, // Larger for young learners
      capHeight: 8,
      ascenderHeight: 10,
      descenderDepth: 4,
      lineSpacing: 20, // More space for practice
      lineGroupSpacing: 30,
      slantAngle: 0, // No slant for beginners
      slantLineSpacing: 8,
      showXHeight: true,
      showCapHeight: false,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: false,
      showSlantLines: false
    },
    elementary: {
      xHeight: 4,
      capHeight: 6,
      ascenderHeight: 8,
      descenderDepth: 3,
      lineSpacing: 15,
      lineGroupSpacing: 25,
      slantAngle: 0, // Manuscript first
      slantLineSpacing: 6,
      showXHeight: true,
      showCapHeight: false,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: false,
      showSlantLines: false
    },
    italic: {
      xHeight: 4,
      capHeight: 6,
      ascenderHeight: 8,
      descenderDepth: 3,
      lineSpacing: 15,
      lineGroupSpacing: 25,
      slantAngle: 7, // Standard italic angle
      slantLineSpacing: 6,
      showXHeight: true,
      showCapHeight: false,
      showAscender: true,
      showDescender: true,
      showLineLabels: true,
      showGrid: false,
      showSlantLines: true
    },
    advanced: {
      xHeight: 3.5, // Smaller for advanced students
      capHeight: 5.5,
      ascenderHeight: 7,
      descenderDepth: 2.5,
      lineSpacing: 12, // Tighter spacing
      lineGroupSpacing: 20,
      slantAngle: 10, // Steeper italic
      slantLineSpacing: 5,
      showXHeight: true,
      showCapHeight: true,
      showAscender: true,
      showDescender: true,
      showLineLabels: false, // Advanced students don't need labels
      showGrid: true,
      showSlantLines: true
    }
  };

  // Update settings
  const updateSetting = (key, value) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  // Apply preset
  const applyPreset = (presetName) => {
    const preset = presets[presetName];
    setSettings(prev => ({ ...prev, ...preset }));
  };

  // Enhanced line calculation with better spacing
  const calculateLines = () => {
    const { marginTop, marginBottom, lineSpacing, lineGroupSpacing, xHeight, capHeight, ascenderHeight, descenderDepth } = settings;
    const availableHeight = CURRENT_HEIGHT - marginTop - marginBottom;

    // Calculate lines per group - optimal for practice (3-4 lines)
    const linesPerGroup = 3;
    const totalGroupHeight = (linesPerGroup - 1) * lineSpacing + lineGroupSpacing;
    const groupsCount = Math.floor(availableHeight / totalGroupHeight);

    const lines = [];

    for (let group = 0; group < groupsCount; group++) {
      for (let lineInGroup = 0; lineInGroup < linesPerGroup; lineInGroup++) {
        const baselineY = marginTop + (group * totalGroupHeight) + (lineInGroup * lineSpacing) + lineSpacing;

        lines.push({
          baseline: baselineY,
          xHeightLine: baselineY - xHeight,
          capHeightLine: baselineY - capHeight,
          ascenderLine: baselineY - ascenderHeight,
          descenderLine: baselineY + descenderDepth,
          groupIndex: group,
          lineIndex: lineInGroup
        });
      }
    }

    return lines;
  };

  // Enhanced slant lines with better spacing
  const generateSlantLines = () => {
    const { slantAngle, slantLineSpacing, marginLeft, marginRight, marginTop, marginBottom } = settings;
    const slantLines = [];

    if (slantAngle === 0) return slantLines; // No slant lines for manuscript

    const angleRad = (slantAngle * Math.PI) / 180;
    const tanAngle = Math.tan(angleRad);

    // Define the writing area boundaries
    const writingAreaLeft = marginLeft;
    const writingAreaRight = CURRENT_WIDTH - marginRight;
    const writingAreaTop = marginTop;
    const writingAreaBottom = CURRENT_HEIGHT - marginBottom;
    const writingAreaWidth = writingAreaRight - writingAreaLeft;
    const writingAreaHeight = writingAreaBottom - writingAreaTop;

    // Calculate projection for slanted lines
    const heightProjection = writingAreaHeight * tanAngle;
    const startX = writingAreaLeft - heightProjection;
    const totalWidth = writingAreaWidth + (2 * heightProjection);
    const numLines = Math.ceil(totalWidth / slantLineSpacing) + 2;

    for (let i = 0; i < numLines; i++) {
      const x = startX + (i * slantLineSpacing);
      const topX = x;
      const bottomX = x + heightProjection;

      slantLines.push({
        x1: topX,
        y1: writingAreaTop,
        x2: bottomX,
        y2: writingAreaBottom
      });
    }

    return slantLines;
  };

  // Enhanced grid lines
  const generateGridLines = () => {
    const { marginLeft, marginRight, marginTop, marginBottom, gridSpacing } = settings;
    const verticalLines = [];
    const horizontalLines = [];

    // Vertical lines within writing area
    for (let x = marginLeft; x <= CURRENT_WIDTH - marginRight; x += gridSpacing) {
      verticalLines.push({
        x,
        y1: marginTop,
        y2: CURRENT_HEIGHT - marginBottom
      });
    }

    // Horizontal lines within writing area
    for (let y = marginTop; y <= CURRENT_HEIGHT - marginBottom; y += gridSpacing) {
      horizontalLines.push({
        y,
        x1: marginLeft,
        x2: CURRENT_WIDTH - marginRight
      });
    }

    return { verticalLines, horizontalLines };
  };

  // Enhanced margin guides
  const generateMarginGuides = () => {
    const { marginTop, marginBottom, marginLeft, marginRight } = settings;

    return {
      top: { x1: 0, y1: marginTop, x2: CURRENT_WIDTH, y2: marginTop },
      bottom: { x1: 0, y1: CURRENT_HEIGHT - marginBottom, x2: CURRENT_WIDTH, y2: CURRENT_HEIGHT - marginBottom },
      left: { x1: marginLeft, y1: 0, x2: marginLeft, y2: CURRENT_HEIGHT },
      right: { x1: CURRENT_WIDTH - marginRight, y1: 0, x2: CURRENT_WIDTH - marginRight, y2: CURRENT_HEIGHT }
    };
  };

  // Export SVG
  const exportSVG = () => {
    const svgElement = svgRef.current;
    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(svgElement);

    const blob = new Blob([svgString], { type: 'image/svg+xml' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'handwriting-practice-sheet.svg';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  // Print template
  const printTemplate = () => {
    const svgElement = svgRef.current;
    const serializer = new XMLSerializer();
    const svgString = serializer.serializeToString(svgElement);

    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Handwriting Practice Sheet</title>
          <style>
            @page { 
              size: A4; 
              margin: 0; 
            }
            body { 
              margin: 0; 
              padding: 0; 
            }
            svg { 
              width: 210mm; 
              height: 297mm; 
            }
          </style>
        </head>
        <body>
          ${svgString}
        </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.print();
  };

  const lines = calculateLines();
  const slantLines = generateSlantLines();
  const { verticalLines, horizontalLines } = generateGridLines();
  const marginGuides = generateMarginGuides();

  // Enhanced slider component
  const BrutalistSlider = ({ label, value, onChange, min, max, step, unit = '' }) => (
    <div className="brutalist-slider-container">
      <div className="brutalist-slider-label">
        <span className="brutalist-label">{label}</span>
        <span className="brutalist-slider-value brutalist-mono">{value}{unit}</span>
      </div>
      <input
        type="range"
        min={min}
        max={max}
        step={step}
        value={value}
        onChange={(e) => onChange(parseFloat(e.target.value))}
        className="brutalist-slider"
      />
    </div>
  );

  // Enhanced checkbox component
  const BrutalistCheckbox = ({ label, checked, onChange }) => (
    <div className="brutalist-checkbox-item" onClick={() => onChange(!checked)}>
      <div className="brutalist-checkbox">
        <input
          type="checkbox"
          checked={checked}
          onChange={() => {}}
          tabIndex={-1}
        />
        <div className="brutalist-checkbox-mark"></div>
      </div>
      <span className="brutalist-checkbox-label">{label}</span>
    </div>
  );

  return (
    <div className="brutalist-container">
      {/* Control Panel */}
      <div className="brutalist-panel no-print">
        {/* Header */}
        <div className="brutalist-section accent">
          <h1 className="brutalist-title">Practice<br/>Sheet<br/>Generator</h1>
          <p className="brutalist-mono" style={{ fontSize: '0.75rem', marginTop: '0.5rem', opacity: 0.8 }}>
            HANDWRITING  A4  EDUCATION
          </p>
        </div>

        {/* Enhanced Presets */}
        <div className="brutalist-section">
          <h3 className="brutalist-section-title">Learning Levels</h3>
          <div className="brutalist-preset-grid">
            {Object.keys(presets).map(preset => (
              <button
                key={preset}
                onClick={() => applyPreset(preset)}
                className="brutalist-btn"
              >
                {preset}
              </button>
            ))}
          </div>
        </div>

        {/* Typography Controls */}
        <div className="brutalist-section">
          <h3 className="brutalist-section-title">Letter Metrics</h3>

          <BrutalistSlider
            label="Line Spacing"
            value={settings.lineSpacing}
            onChange={(value) => updateSetting('lineSpacing', value)}
            min={10}
            max={25}
            step={0.5}
            unit="mm"
          />

          <BrutalistSlider
            label="Group Spacing"
            value={settings.lineGroupSpacing}
            onChange={(value) => updateSetting('lineGroupSpacing', value)}
            min={18}
            max={40}
            step={1}
            unit="mm"
          />

          <BrutalistSlider
            label="X-Height"
            value={settings.xHeight}
            onChange={(value) => updateSetting('xHeight', value)}
            min={2.5}
            max={8}
            step={0.5}
            unit="mm"
          />

          <BrutalistSlider
            label="Cap Height"
            value={settings.capHeight}
            onChange={(value) => updateSetting('capHeight', value)}
            min={4}
            max={12}
            step={0.5}
            unit="mm"
          />

          <BrutalistSlider
            label="Ascender Height"
            value={settings.ascenderHeight}
            onChange={(value) => updateSetting('ascenderHeight', value)}
            min={5}
            max={15}
            step={0.5}
            unit="mm"
          />

          <BrutalistSlider
            label="Descender Depth"
            value={settings.descenderDepth}
            onChange={(value) => updateSetting('descenderDepth', value)}
            min={2}
            max={8}
            step={0.5}
            unit="mm"
          />
        </div>

        {/* Slant Controls */}
        <div className="brutalist-section">
          <h3 className="brutalist-section-title">Italic Style</h3>

          <BrutalistSlider
            label="Slant Angle"
            value={settings.slantAngle}
            onChange={(value) => updateSetting('slantAngle', value)}
            min={0}
            max={20}
            step={1}
            unit=""
          />

          <BrutalistSlider
            label="Slant Spacing"
            value={settings.slantLineSpacing}
            onChange={(value) => updateSetting('slantLineSpacing', value)}
            min={3}
            max={10}
            step={0.5}
            unit="mm"
          />
        </div>

        {/* Guide Lines */}
        <div className="brutalist-section">
          <h3 className="brutalist-section-title">Guide Lines</h3>
          <div className="brutalist-checkbox-container">
            <BrutalistCheckbox
              label="Slant Lines"
              checked={settings.showSlantLines}
              onChange={(value) => updateSetting('showSlantLines', value)}
            />
            <BrutalistCheckbox
              label="X-Height Guide"
              checked={settings.showXHeight}
              onChange={(value) => updateSetting('showXHeight', value)}
            />
            <BrutalistCheckbox
              label="Cap Height Guide"
              checked={settings.showCapHeight}
              onChange={(value) => updateSetting('showCapHeight', value)}
            />
            <BrutalistCheckbox
              label="Ascender Guide"
              checked={settings.showAscender}
              onChange={(value) => updateSetting('showAscender', value)}
            />
            <BrutalistCheckbox
              label="Descender Guide"
              checked={settings.showDescender}
              onChange={(value) => updateSetting('showDescender', value)}
            />
            <BrutalistCheckbox
              label="Line Labels"
              checked={settings.showLineLabels}
              onChange={(value) => updateSetting('showLineLabels', value)}
            />
            <BrutalistCheckbox
              label="Precision Grid"
              checked={settings.showGrid}
              onChange={(value) => updateSetting('showGrid', value)}
            />
          </div>
        </div>

        {/* Export Controls */}
        <div className="brutalist-section">
          <h3 className="brutalist-section-title">Export</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem', marginTop: '1rem' }}>
            <button
              onClick={printTemplate}
              className="brutalist-btn-accent"
            >
              <Printer size={16} />
              Print Sheet
            </button>
            <button
              onClick={exportSVG}
              className="brutalist-btn-accent"
            >
              <Download size={16} />
              Download SVG
            </button>
          </div>
        </div>
      </div>

      {/* Preview Area */}
      <div className="brutalist-canvas-area">
        <div 
          className="brutalist-canvas" 
          style={{ width: CURRENT_WIDTH * SCALE_FACTOR, height: CURRENT_HEIGHT * SCALE_FACTOR }}
        >
          <svg
            ref={svgRef}
            width={CURRENT_WIDTH * SCALE_FACTOR}
            height={CURRENT_HEIGHT * SCALE_FACTOR}
            viewBox={`0 0 ${CURRENT_WIDTH} ${CURRENT_HEIGHT}`}
            className="practice-sheet-text"
          >
            {/* Page background */}
            <rect width={CURRENT_WIDTH} height={CURRENT_HEIGHT} fill="white" />

            {/* Margin guides */}
            <g opacity="0.3">
              <line {...marginGuides.top} stroke={settings.marginColor} strokeWidth="0.5" strokeDasharray="2,2" />
              <line {...marginGuides.bottom} stroke={settings.marginColor} strokeWidth="0.5" strokeDasharray="2,2" />
              <line {...marginGuides.left} stroke={settings.marginColor} strokeWidth="0.5" strokeDasharray="2,2" />
              <line {...marginGuides.right} stroke={settings.marginColor} strokeWidth="0.5" strokeDasharray="2,2" />
            </g>

            {/* Define clipping path for writing area only */}
            <defs>
              <clipPath id="writingAreaClip">
                <rect 
                  x={settings.marginLeft} 
                  y={settings.marginTop} 
                  width={CURRENT_WIDTH - settings.marginLeft - settings.marginRight} 
                  height={CURRENT_HEIGHT - settings.marginTop - settings.marginBottom} 
                />
              </clipPath>
            </defs>

            {/* Slant lines - clipped to writing area only */}
            {settings.showSlantLines && settings.slantAngle > 0 && (
              <g opacity={settings.slantLineOpacity} clipPath="url(#writingAreaClip)">
                {slantLines.map((line, i) => (
                  <line
                    key={`slant-${i}`}
                    x1={line.x1}
                    y1={line.y1}
                    x2={line.x2}
                    y2={line.y2}
                    stroke={settings.slantLineColor}
                    strokeWidth="0.2"
                  />
                ))}
              </g>
            )}

            {/* Grid lines */}
            {settings.showGrid && (
              <g opacity={settings.guidelineOpacity}>
                {verticalLines.map((line, i) => (
                  <line
                    key={`v-${i}`}
                    x1={line.x}
                    y1={line.y1}
                    x2={line.x}
                    y2={line.y2}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.1"
                    strokeDasharray="1,1"
                  />
                ))}
                {horizontalLines.map((line, i) => (
                  <line
                    key={`h-${i}`}
                    x1={line.x1}
                    y1={line.y}
                    x2={line.x2}
                    y2={line.y}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.1"
                    strokeDasharray="1,1"
                  />
                ))}
              </g>
            )}

            {/* Typography lines */}
            {lines.map((lineSet, i) => (
              <g key={`line-set-${i}`}>
                {/* Line Labels - enhanced positioning */}
                {settings.showLineLabels && lineSet.lineIndex === 0 && (
                  <g opacity="0.7" className="practice-sheet-labels">
                    {/* Ascender label */}
                    {settings.showAscender && (
                      <text
                        x={settings.marginLeft - 12}
                        y={lineSet.ascenderLine + 1}
                        fontSize="2.5"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="ArkSans, Arial, sans-serif"
                        fontWeight="bold"
                      >
                        A
                      </text>
                    )}

                    {/* Cap height label */}
                    {settings.showCapHeight && (
                      <text
                        x={settings.marginLeft - 12}
                        y={lineSet.capHeightLine + 1}
                        fontSize="2.5"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="ArkSans, Arial, sans-serif"
                        fontWeight="bold"
                      >
                        C
                      </text>
                    )}

                    {/* X-height label */}
                    {settings.showXHeight && (
                      <text
                        x={settings.marginLeft - 12}
                        y={lineSet.xHeightLine + 1}
                        fontSize="2.5"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="ArkSans, Arial, sans-serif"
                        fontWeight="bold"
                      >
                        X
                      </text>
                    )}

                    {/* Baseline label */}
                    <text
                      x={settings.marginLeft - 12}
                      y={lineSet.baseline + 1}
                      fontSize="2.5"
                      fill="#666"
                      textAnchor="middle"
                      fontFamily="ArkSans, Arial, sans-serif"
                      fontWeight="bold"
                    >
                      B
                    </text>

                    {/* Descender label */}
                    {settings.showDescender && (
                      <text
                        x={settings.marginLeft - 12}
                        y={lineSet.descenderLine + 1}
                        fontSize="2.5"
                        fill="#666"
                        textAnchor="middle"
                        fontFamily="ArkSans, Arial, sans-serif"
                        fontWeight="bold"
                      >
                        D
                      </text>
                    )}
                  </g>
                )}

                {/* Baseline - always shown, thicker */}
                <line
                  x1={settings.marginLeft}
                  y1={lineSet.baseline}
                  x2={CURRENT_WIDTH - settings.marginRight}
                  y2={lineSet.baseline}
                  stroke={settings.baselineColor}
                  strokeWidth="0.6"
                  opacity={settings.baselineOpacity}
                />

                {/* X-height line */}
                {settings.showXHeight && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.xHeightLine}
                    x2={CURRENT_WIDTH - settings.marginRight}
                    y2={lineSet.xHeightLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.3"
                    opacity={settings.guidelineOpacity}
                  />
                )}

                {/* Cap height line */}
                {settings.showCapHeight && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.capHeightLine}
                    x2={CURRENT_WIDTH - settings.marginRight}
                    y2={lineSet.capHeightLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.2"
                    opacity={settings.guidelineOpacity}
                    strokeDasharray="4,2"
                  />
                )}

                {/* Ascender line */}
                {settings.showAscender && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.ascenderLine}
                    x2={CURRENT_WIDTH - settings.marginRight}
                    y2={lineSet.ascenderLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.3"
                    opacity={settings.guidelineOpacity}
                  />
                )}

                {/* Descender line */}
                {settings.showDescender && (
                  <line
                    x1={settings.marginLeft}
                    y1={lineSet.descenderLine}
                    x2={CURRENT_WIDTH - settings.marginRight}
                    y2={lineSet.descenderLine}
                    stroke={settings.guidelineColor}
                    strokeWidth="0.3"
                    opacity={settings.guidelineOpacity}
                  />
                )}
              </g>
            ))}

            {/* Enhanced Legend */}
            <g transform="translate(15, 15)">
              <rect x="0" y="0" width="55" height="22" fill="white" fillOpacity="0.95" stroke="#ddd" strokeWidth="0.1"/>
              <text x="2" y="4" fontSize="2.5" fill="#333" fontWeight="bold" fontFamily="ArkSans, Arial, sans-serif">
                Handwriting Practice Sheet
              </text>

              {settings.showLineLabels && (
                <g fontSize="1.4" fill="#666" fontFamily="ArkSans, Arial, sans-serif">
                  {settings.showAscender && <text x="2" y="8">A = Ascender Line</text>}
                  {settings.showCapHeight && <text x="2" y="11">C = Cap Height</text>}
                  {settings.showXHeight && <text x="2" y="14">X = X-Height</text>}
                  <text x="2" y="17">B = Baseline</text>
                  {settings.showDescender && <text x="2" y="20">D = Descender Line</text>}
                </g>
              )}

              {!settings.showLineLabels && (
                <g>
                  <line x1="2" y1="8" x2="8" y2="8" stroke={settings.baselineColor} strokeWidth="0.6"/>
                  <text x="10" y="9" fontSize="1.4" fill="#333" fontFamily="ArkSans, Arial, sans-serif">Baseline</text>
                  {settings.showSlantLines && settings.slantAngle > 0 && (
                    <>
                      <line x1="2" y1="12" x2="8" y2="12" stroke={settings.slantLineColor} strokeWidth="0.2"/>
                      <text x="10" y="13" fontSize="1.4" fill="#333" fontFamily="ArkSans, Arial, sans-serif">
                        Slant {settings.slantAngle}
                      </text>
                    </>
                  )}
                </g>
              )}
            </g>
          </svg>
        </div>
      </div>
    </div>
  );
};

export default HandwritingTemplateGenerator;
