import { create } from 'zustand';

// A4 dimensions in mm
export const A4_CONFIG = {
  width: 210,
  height: 297,
  dpi: 300,
  pixelWidth: 2480,
  pixelHeight: 3508,
};

// Typography ratio presets
export const TYPOGRAPHY_PRESETS = {
  italic: {
    name: 'Italic Foundational',
    xHeight: 0.5,
    capHeight: 0.7,
    ascender: 0.75,
    descender: 0.25,
    slantAngle: 7,
    lineSpacing: 2.5,
  },
  copperplate: {
    name: 'Copperplate',
    xHeight: 0.33,
    capHeight: 0.66,
    ascender: 1.0,
    descender: 0.33,
    slantAngle: 12,
    lineSpacing: 3.0,
  },
  spencerian: {
    name: 'Spencerian',
    xHeight: 0.4,
    capHeight: 0.6,
    ascender: 0.8,
    descender: 0.3,
    slantAngle: 15,
    lineSpacing: 2.8,
  },
  modern: {
    name: 'Modern Calligraphy',
    xHeight: 0.45,
    capHeight: 0.65,
    ascender: 0.85,
    descender: 0.35,
    slantAngle: 10,
    lineSpacing: 3.2,
  },
  minimal: {
    name: 'Minimal',
    xHeight: 0.5,
    capHeight: 0.7,
    ascender: 0.75,
    descender: 0.25,
    slantAngle: 7,
    lineSpacing: 2.0,
  },
};

export const useTemplateStore = create((set, get) => ({
  // Template configuration
  paperSize: 'A4',
  orientation: 'portrait',
  margins: { top: 20, right: 20, bottom: 20, left: 20 },

  // Typography metrics
  baseSize: 20, // Base line height in mm
  currentPreset: 'italic',
  metrics: { ...TYPOGRAPHY_PRESETS.italic },

  // Grid options
  gridType: 'ruled',
  gridOpacity: 0.3,
  gridColor: '#9ca3af',
  baselineColor: '#374151',
  slantLineColor: '#ef4444',
  showSlantLines: true,
  showGrid: true,

  // Layout
  numberOfLines: 10,
  letterSpacing: 5, // mm
  wordSpacing: 15, // mm

  // Actions
  setMetric: (key, value) => set((state) => ({
    metrics: { ...state.metrics, [key]: value },
    currentPreset: 'custom'
  })),

  setPreset: (presetName) => set({
    currentPreset: presetName,
    metrics: { ...TYPOGRAPHY_PRESETS[presetName] }
  }),

  setBaseSize: (size) => set({ baseSize: size }),

  setGridOption: (key, value) => set({ [key]: value }),

  setLayoutOption: (key, value) => set({ [key]: value }),

  setMargins: (margins) => set({ margins }),

  // Calculated values
  getCalculatedMetrics: () => {
    const state = get();
    const { baseSize, metrics } = state;

    return {
      xHeight: baseSize * metrics.xHeight,
      capHeight: baseSize * metrics.capHeight,
      ascenderHeight: baseSize * metrics.ascender,
      descenderDepth: baseSize * metrics.descender,
      lineSpacing: baseSize * metrics.lineSpacing,
      slantAngle: metrics.slantAngle,
    };
  },

  getCanvasSize: () => {
    const state = get();
    const scale = 2.5; // Scale for screen display - much larger for better visibility

    if (state.orientation === 'landscape') {
      return {
        width: A4_CONFIG.height * scale,
        height: A4_CONFIG.width * scale,
      };
    }

    return {
      width: A4_CONFIG.width * scale,
      height: A4_CONFIG.height * scale,
    };
  },
}));
